public class Point {

		private String label;
		private int value;

		public Point(String l, int v) {
			// TODO Auto-generated constructor stub
			label = l;
			value = v;
		}

		public String getLabel() {
			return label;
		}

		public int getValue() {
			return value;
		}

		public void setLabel(String label) {
			this.label = label;
		}

		public void setValue(int value) {
			this.value = value;
		}

	}